import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { TextareaAutosize, FormHelperText } from "@material-ui/core";
import { ListGroup, Button } from "react-bootstrap";
import axios from 'axios';

class NamingConventions extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      status: "",
      project: "", //encodeURI("\\SYSHADHA22"),
      sandbox: "", //encodeURI("D:\\Sandbox\\SYSHADHA22"),
    }

    this.handleButtonClick = this.handleButtonClick.bind(this);
    this.handleChangeProject = this.handleChangeProject.bind(this);
    this.handleChangeSandbox = this.handleChangeSandbox.bind(this);

  }

  async handleButtonClick(event) {
    if (this.state.project != "" && this.state.sandbox != "") {
      let featureRequest = `http://127.0.0.1:5000/api/ims/run-naming-conventions/${this.state.project}&${this.state.sandbox}`;
      axios.post(featureRequest).then(response => {
        console.log(response.data);
        this.setState({ status: response.data })
        if (this.state.status == "Success!") {
          setTimeout(() => {
            var response = {
              file: `http://127.0.0.1:5000/api/ims/run-naming-conventions/download/${this.state.project}`,
            };
            //download:
            window.location.href = response.file;
            // you could also do:
            //window.open(response.file);
          }, 100);
        }
      })
    }
    else {
      alert("Empty Fields!")
    }

  }


  handleChangeProject(event) {
    this.setState({ project: encodeURI("\\" + event.target.value) })
    console.log(this.state);
  }

  handleChangeSandbox(event) {
    this.setState({ sandbox: encodeURI(event.target.value) })
    console.log(this.state);
  }

  render() {
    return (
      <div class="prm-background">
        <div style={{ display: "flex", justifyContent: "space-evenly", flexDirection: 'column', margin: "2%" }}>
          <h1 className='script-title'>Naming conventions for X</h1>

          <form style={{ alignSelf: "center" }}>
            {/*
            <p>Project:</p>
            <select>
              <option disabled selected value>-- choose a project --</option>
              <option value="SYSHADHA22">SYSHADHA22</option>
              <option value="ADC420">ADC420</option>
            </select>
            */}

            
            <label>
              <p>Project Name:</p>
              <input type="text" name={this.state.project} onChange={this.handleChangeProject} />
            </label><br />

            <label>
              <p>Sandbox (folder path):</p>
              <input type="text" name="folder" onChange={this.handleChangeSandbox} />
            </label><br />

          </form>
          <Button className='scripts-btn' onClick={this.handleButtonClick}>Generate excel</Button>
          <label style={{ alignSelf: "center" }}>{this.state.status}</label>
        </div>
      </div>
    );
  }
}

const btnStyle = {
  height: '10vh',
  width: '25vw',
  fontSize: '150%',
  position: 'absolute', left: '50%', top: '25%',
  transform: 'translate(-50%, -50%)'
}

export default NamingConventions;
