import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { Button, InputGroup } from "react-bootstrap";
import axios from 'axios';
import { ListGroup } from 'react-bootstrap';

class TransitionFromAnalyzerToAccepted extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Epics: []
    };
    this.makeTransitionFromAnalyzedToAccepted = this.makeTransitionFromAnalyzedToAccepted.bind(this)
  }
  async makeTransitionFromAnalyzedToAccepted() {
    let postRequest = "http://127.0.0.1:5000/api/jira/epicinanalyzed";
    axios.post(postRequest)
      .then(response => {
        this.setState({
          Epics: response.data
        })
      })
  }

  render() {
    return (
      <div class="prm-background">
        <PrmNavBar></PrmNavBar>
        <h1 style={{ textAlign: "center", color: "#0FA3B1" }}>Epics from Analyzed to Accepted if it has a match issue in HGEN1L1 in Accepted</h1>
        <div class="btn-make-transition">
          <Button variant="primary" onClick={this.makeTransitionFromAnalyzedToAccepted}>Make transition</Button>
        </div>
        <div class="epic-container">
          {this.state.epics}
        </div>
        {
          this.state.Epics.map(item =>
            <ListGroup.Item >
              Epic from ADC420ha22 <a action target="_blank" rel="noopener noreferrer" href={"https://jira-adas.zone2.agileci.conti.de/browse/" + item["hgen1l1_issue"]["key"]}> {item["hgen1l1_issue"]["key"]}</a>
            </ListGroup.Item>

          )
        }
      </div>
    );
  }
}
export default TransitionFromAnalyzerToAccepted;
