import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { TextareaAutosize, FormHelperText } from "@material-ui/core";
import { ListGroup, Button } from "react-bootstrap";

class FolderStructure extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      status: "",
    }

    this.handleButtonClick=this.handleButtonClick.bind(this);

  }

  handleButtonClick(event){
    let featureRequest =
      "http://127.0.0.1:5000/api/ims/run_folder_structure";
    fetch(featureRequest, {method:'POST'})
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          status: data,
        });
      });

  }
  render() {
    return (
      <div class="prm-background">
        <PrmNavBar></PrmNavBar>
        <div style={{display: "flex", justifyContent: "space-evenly", margin:"2%"}}>
          <h1 style={{textAlign:"center", color:"#0FA3B1"}}>FolderStructure</h1>
          <Button onClick={this.handleButtonClick}>Start app</Button>
        </div>
      </div>
    );
  }
}

const btnStyle={
  height:'10vh',
  width:'25vw',
  fontSize:'150%',
  position: 'absolute', left: '50%', top: '25%',
  transform: 'translate(-50%, -50%)'
}

export default FolderStructure;
