import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { TextareaAutosize } from "@material-ui/core";
import Button from "react-bootstrap"

class ADCU_metrics extends React.Component {
  constructor(props) {
    super(props);
    this.state = {ticket : "" };
    this.handleClickEvent = this.handleClickEvent.bind(this);
  }
  handleClickEvent(event) {
    let featureRequest =
      "http://127.0.0.1:5000/api/jira/statistics/adcu_metrics";
    fetch(featureRequest)
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          ticket: data,
        });
        this.setState({
          redirectToTicket: true,
        });
      });
  }
  render() {
    return (
      <div class="prm-background">
        <PrmNavBar></PrmNavBar>
    <h1 style={{textAlign:"center", color:"#0FA3B1"}}>Local path: {this.state.ticket}</h1>
    <Button onClick={this.handleClickEvent}>Go</Button>
        
      </div>
    );
  }
}

export default ADCU_metrics;
