import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { TextareaAutosize, FormHelperText } from "@material-ui/core";
import { ListGroup, Button } from "react-bootstrap";

class FolderStructure extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      status: "",
    }

    this.handleButtonClick=this.handleButtonClick.bind(this);

  }

  handleButtonClick(event){

    let featureRequest =
      "http://127.0.0.1:5000/api/ims/run_folder_structure";
    fetch(featureRequest, {method:'POST'})
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          status: data,
        });
      });
  }


  render() {
    return (
      <div class="prm-background">
        <div style={{display: "flex", justifyContent: "space-evenly", flexDirection:'column', margin:"2%"}}>
          <h1 className='script-title'>Folder Structure</h1>
          <Button className='scripts-btn' onClick={this.handleButtonClick}>Start app</Button>
        </div>
      </div>
    );
  }
}

const btnStyle={
  height:'10vh',
  width:'25vw',
  fontSize:'150%',
  position: 'absolute', left: '50%', top: '25%',
  transform: 'translate(-50%, -50%)'
}

export default FolderStructure;
