import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { TextareaAutosize, FormHelperText } from "@material-ui/core";
import { ListGroup, Button } from "react-bootstrap";

class CreateSS extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ticketInfo:[],
    }

    this.handleButtonClick=this.handleButtonClick.bind(this);

  }

  handleButtonClick(event){
    let featureRequest =
      "http://127.0.0.1:5000/api/jira/create-stories";
    fetch(featureRequest, {method:'POST'})
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          ticketInfo: data,
        });
        console.log(data);
      });
  }
  render() {
    return (
      <div class="prm-background">
        <PrmNavBar></PrmNavBar>
        <div style={{display: "flex", justifyContent: "space-evenly", margin:"2%"}}>
          <h1 style={{textAlign:"center", color:"#0FA3B1"}}>Stories for ADC420HA22</h1>
          <Button onClick={this.handleButtonClick}>Create Stories for epics in accepted</Button>
        </div> 
        {
          console.log(this.state.ticketInfo)
        }
        {
            this.state.ticketInfo.map(item =>
              <div>

              <ListGroup.Item>
                Story <a target="_blank" href={"https://jira-c-adas-test.zone2.agileci.conti.de/browse/" + item}>{item}</a> and its subtasks created succesfully.
              </ListGroup.Item>
              </div>
            )
            }     
      </div>
    );
  }
}

const btnStyle={
  height:'10vh',
  width:'25vw',
  fontSize:'150%',
  position: 'absolute', left: '50%', top: '25%',
  transform: 'translate(-50%, -50%)'
}

export default CreateSS;
