import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { TextareaAutosize, withTheme } from "@material-ui/core";
import { ListGroup } from "react-bootstrap";
import { blue } from "@material-ui/core/colors";




const btnStyle={
  margin:50,

}

class CreateEpicsHA22 extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      tickets:""
    }
    this.handleClickEvent=this.handleClickEvent.bind(this);
  }

  handleClickEvent(event){
    let featureRequest =
    "http://127.0.0.1:5000/api/jira/CreateEpicsHA22" ;
  fetch(featureRequest,{method:"POST"})
    .then((response) => response.json())
    .then((data) => {
      this.setState({
        tickets: data,
      });
      // console.log(data);
    });
  }
  render() {
    return (
      <div class="prm-background">
        <PrmNavBar></PrmNavBar>
            <h1 style={{textAlign:"center", color:"#0FA3B1"}}>Create epics in ADC420HA22</h1> 
            <h5 style={{textAlign:"center", color:"#0FA3B1"}}>This script clones Feature Requests from project HGEN1L1 with Area of Resposnsibilty ADCU into the project ADC420HA22  </h5>
            <button  onClick={this.handleClickEvent} style={btnStyle}>Run Script </button>

            { Object.keys(this.state.tickets).map(item =>
             <div>
                <ListGroup.Item action target="_blank" rel="noopener noreferrer"  href={"https://jira-c-adas-test.zone2.agileci.conti.de/browse/" + this.state.tickets[item]}>
                  {
                    this.state.tickets[item]
                  }
                </ListGroup.Item>
              </div>
              ) }
            </div>
    );
  }

}

export default CreateEpicsHA22;
