import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { TextareaAutosize, FormHelperText } from "@material-ui/core";
import { Button, ListGroup } from "react-bootstrap";
import axios from "axios";

class CreateAS extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      epicKeys:[],
      analysisKeys: [],
      tickets:'',
      show: "false",
    }

    this.handleButtonClick=this.handleButtonClick.bind(this);

  }

   handleButtonClick(event){
    let featureRequest =
      "http://127.0.0.1:5000/api/jira/create-analysis";
    fetch(featureRequest, {method:'POST'})
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          epicKeys: Object.keys(data),
          analysisKeys: this.getValues(data),
          tickets: data,
          show: "true",
        });
        console.log(this.state.tickets);
      });      

      // axios.post('http://127.0.0.1:5000/api/jira/create-analysis').then(response=>{
      //   console.log(response);
      //   alert(Object.keys(response.data[1]));
      // }).catch(error=>{console.log(error)})
  }

  getValues(dictionary){
    var value=[];
    var i=0;
    for(var key in dictionary){
      value[i] = dictionary[key];
      i++;
    }
      return value;
  }


  render() {
    return (
      <div class="prm-background">
        <PrmNavBar></PrmNavBar>
            <div style={{display: "flex", justifyContent: "space-evenly", margin:"2%"}}>
              <h1 style={{textAlign:"center", color:"#0FA3B1"}}>Analysis Stories for ADC420HA22</h1>
              <Button onClick={this.handleButtonClick}>Create Analysis story for epics in classified</Button>
            </div>
            {
              console.log(this.state.tickets)
            }

            {
            Object.keys(this.state.tickets).map(item =>
              <div>
              <ListGroup.Item>
                Analysis story <a target="_blank" href={"https://jira-c-adas-test.zone2.agileci.conti.de/browse/" + this.state.tickets[item]}>{this.state.tickets[item]}</a> created for <a target="_blank" href={"https://jira-c-adas-test.zone2.agileci.conti.de/browse/" + item}>{item}</a>
              </ListGroup.Item>
              </div>
              )
            }
      </div> 


    );
  }
}

const btnStyle={
  height:'10vh',
  width:'25vw',
  fontSize:'150%',
  position: 'absolute', left: '50%', top: '25%',
  transform: 'translate(-50%, -50%)'
}

const listContent={
  textAlign:'center',
}

export default CreateAS;
