import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { Multiselect } from "multiselect-react-dropdown";
import { Button } from "react-bootstrap";
import axios from "axios";

class CustomModel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      options: [
        { name: "Start Date", id: 1 },
        { name: "Asignee", id: 2 },
        { name: "Area", id: 3 },
        { name: "Priority", id: 4 },
        { name: "Month", id: 5 },
      ],
      chosen: [],
    };

    this.onSelect = this.onSelect.bind(this);
    this.sendClassifier = this.sendClassifier.bind(this);
  }

  onSelect(selectedList, selectedItem) {
    this.setState((state) => state.chosen.push(selectedItem));
  }

  onRemove(selectedList, removedItem) {}

  sendClassifier() {
    console.log(this.state.chosen[1]);
    let url = "http://127.0.0.1:5000/api/ml/classifier";
    axios
      .post(url, this.state.chosen)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    return (
      <div>
        <PrmNavBar></PrmNavBar>

        <Multiselect
          options={this.state.options} // Options to display in the dropdown
          selectedValues={this.state.selectedValue} // Preselected value to persist in dropdown
          onSelect={this.onSelect} // Function will trigger on select event
          onRemove={this.onRemove} // Function will trigger on remove event
          displayValue="name" // Property name to display in the dropdown options
        />
        <div class="mt-2"></div>
        <Button onClick={this.sendClassifier}>Add Model</Button>
      </div>
    );
  }
}

export default CustomModel;
