import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { Button } from "react-bootstrap";
import { TextareaAutosize } from "@material-ui/core";
import axios from 'axios';






class AdcuStatistics extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      status: "",
      scriptHasBeenRan_HAD : false,
      scriptHasBeenRan_PAD : false,
    }
    this.handleButtonClick_HAD= this.handleButtonClick_HAD.bind(this);
    this.handleButtonClick_PAD= this.handleButtonClick_PAD.bind(this);
  }

  async handleButtonClick_HAD(event){
    let featureRequest =
      "http://127.0.0.1:5000/api/jira/generate-excel-had";
    fetch(featureRequest, {method:'GET'})
      .then((response) => response)
      .then((data) => {
        this.setState({
          status: data,
          scriptHasBeenRan_HAD: true
        });
      });   
  }
  async handleButtonClick_PAD(event){
    let featureRequest =
      "http://127.0.0.1:5000/api/jira/generate-excel-pad";
    fetch(featureRequest, {method:'GET'})
      .then((response) => response)
      .then((data) => {
        this.setState({
          status: data,
          scriptHasBeenRan_PAD: true
        });
      });   
  }
  render() {
    return (
      <div class="prm-background">
        <PrmNavBar />
        <h1 style={{ textAlign: "center", color: "#0FA3B1" }}>SW2 ADCU Weekly Metrics</h1>
        <div style={{ textAlign: "center", color: "#0FA3B1" }} class="statistics">
          <Button variant="dark" onClick={this.handleButtonClick_HAD}>Run Scripts for HAD</Button>
        </div>
        <div style={{ textAlign: "center", color: "#0FA3B1" }} class="download-excel-had">
        <Button variant="dark" disabled = {this.state.scriptHasBeenRan_HAD == false} href="http://127.0.0.1:5000/download-excel-had"> Download excel for HAD </Button>

        <div> <br></br> </div>
        </div>
        <div style={{ textAlign: "center", color: "#0FA3B1" }} class="statistics2">
          <Button variant="dark" onClick={this.handleButtonClick_PAD}>Run Scripts for PAD</Button>
        </div>
        <div style={{ textAlign: "center", color: "#0FA3B1" }} class="download-excel-pad">
        <Button variant="dark" disabled = {this.state.scriptHasBeenRan_PAD == false} href="http://127.0.0.1:5000/download-excel-pad"> Download excel for PAD </Button>
        </div>
      </div>


    );
  }
}

export default AdcuStatistics;
