import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import PrmNavBar from "./PrmNavBar";
import { Button } from "react-bootstrap";

class Admin extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      token: "",
      bound: "",
    };

    this.handleMaxBound = this.handleMaxBound.bind(this);
    this.handleSecurityToken = this.handleSecurityToken.bind(this);
    this.handleRequest = this.handleRequest.bind(this);
  }

  handleRequest() {
    console.log(this.state.token + " " + this.state.bound);
    let url = "/api/jira/" + this.state.token + "/" + this.state.bound;
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      });
  }

  handleSecurityToken(event) {
    this.setState({ token: event.target.value });
  }

  handleMaxBound(event) {
    this.setState({ bound: event.target.value });
  }

  render() {
    return (
      <div>
        <PrmNavBar></PrmNavBar>
        <div class="prediction-form ml-2">
          <input
            class="text-box"
            type="text"
            placeholder="Security Token"
            onChange={this.handleSecurityToken}
          />
          <input
            class="text-box"
            type="text"
            placeholder="Ticket Max Bound"
            onChange={this.handleMaxBound}
          />
          <Button onClick={this.handleRequest}>Refresh Local Tickets</Button>
        </div>
      </div>
    );
  }
}

export default Admin;
